import React from 'react'

const edit = (name, items, setItems) => () => {
  setItems(
    [...items.filter(item => item.name !== name), items.find(item => item.name == name)]
  )
}

const remove = (name, items, setItems) => () => {
  setItems(
    [...items.filter(item => item.name !== name)]
  )
}

const Item = ({ data, items, setItems }) => (
  <div className='item'>
    <img src={data.image}/>
    <div className='box'>
      <h3><input>{data.name}</input></h3>
      <p>{data.description}</p>
      <button onClick={edit(data.name, items, setItems)}>Edit</button>
      <button onClick={remove(data.name, items, setItems)}>Remove</button>
    </div>
    <h4>{data.rating}/5</h4>
  </div>
)


export default Item
