import React from 'react'

import Item from './Item'

const edit = (name) => () => {
  setItems(
    [...items.filter(item => item.name !== name), items.find(item => item.name == name)]
  )
}

const remove = (name) => () => {
  setItems(
    [...items.filter(item => item.name !== name)]
  )
}

const ItemList = ({ items, setItems }) => {

  return (
    <div className='item-list'>
      {
        items.map((item, idx) => <Item key={idx} items={items} data={item} setItems={setItems}/>)
      }
      <div className='add'>
        <p>Add item</p>
      </div>
    </div>
  )
}

export default ItemList
