import React from 'react'
import { useForm } from '../CustomHooks'

const EditableItem = ({data, save}) => {
  const {inputs, handleInputChange } = useForm()

  return (
  <div className='item'>
    <input type="text" value={inputs.image || data.image} onChange={handleInputChange} />
    <input type="text" value={inputs.name || data.name} onChange={handleInputChange} />
    <input type="text" value={inputs.description || data.description} onChange={handleInputChange} />
    <input type="text" value={inputs.rating || data.rating} onChange={handleInputChange} />
    <button onClick={save(data.name)}>Save</button>
  </div>
)}

export default EditableItem
